import React, { useState, useEffect } from "react";
import { Row, Col, Button, Typography } from "antd";
import { useRouter } from "next/router";
import { useSelector } from "react-redux";
const { Text, Paragraph } = Typography;
import { FacebookProvider, Share } from "react-facebook";

import { ADD_URL } from "../reducers/answer";

import {
	halfCol,
	fullCol,
	centerCol,
	WidthImg,
	HeightImg,
	Texty,
	ActionBtn,
} from "../assets/css/style_result";
import storage from "../back/cloudStorage";
import Feature from "./Feature";
import result from "../assets/result.json";

const Result = ({ name }) => {
	const v = result[name];
	const dispatch = useDispatch();
	const router = useRouter();

	const { url } = useSelector((state) => state.answer);
	const { isDirect } = useSelector((state) => state.answer);

	const [screenshotUrl, setScreenshotUrl] = useState(null);
	const [thumbUrl, setThumbUrl] = useState(null);

	useEffect(() => {
		setScreenshotUrl(url["screenshotUrl"]);
		setThumbUrl(url["thumbUrl"]);
	}, [url]);

	useEffect(() => {
		if (isDirect && !thumbUrl) {
			storage
				.ref()
				.child(v["screenshot"])
				.getDownloadURL()
				.then((url) => {
					setScreenshotUrl(url);
				})
				.catch((err) => {
					return;
				});

			storage
				.ref()
				.child(v["thumbnail"])
				.getDownloadURL()
				.then((url) => {
					setThumbUrl(url);
				})
				.catch((err) => {
					return;
				});
		}
	}, [isDirect]);

	useEffect(() => {
		if (isDirect && screenshotUrl && thumbUrl) {
			dispatch({
				type: ADD_URL,
				data: {
					screenshotUrl: screenshotUrl,
					thumbUrl: thumbUrl,
				},
			});
		}
	}, [screenshotUrl, thumbUrl]);

	useEffect(() => {
		try {
			Kakao.init("0bea3f30baffa17a95149953e8addd7e");
		} catch (err) {
			console.log(err);
		}
	}, []);

	const toTop = () => {
		router.push("/");
	};

	const toKakao = () => {
		try {
			Kakao.Link.sendDefault({
				objectType: "feed",
				content: {
					title: "내가 의학드라마 주인공이라면?",
					description: v["title"],
					imageUrl: thumbUrl,
					link: {
						mobileWebUrl: "https://doctor-mbti.web.app",
						webUrl: "https://doctor-mbti.web.app",
					},
				},
				buttons: [
					{
						title: "나도 해볼래요",
						link: {
							mobileWebUrl: "https://doctor-mbti.web.app",
							webUrl: "https://doctor-mbti.web.app",
						},
					},
				],
			});
		} catch (err) {
			console.log(err);
		}
	};

	return (
		<>
			<Row justify="center">
				<Texty top={50} font={15}>
					내가 의학드라마 주인공이라면
				</Texty>
			</Row>
			<Row justify="center">
				<Texty strong top={15} font={30} color={"#be2edd"}>
					{v["title"]}
				</Texty>
			</Row>
			<Row justify="center">
				<Texty strong top={5} font={15}>
					{v["subtitle"]}
				</Texty>
			</Row>
			<Row justify="center">
				<Col {...centerCol}>
					<WidthImg src={thumbUrl} />
				</Col>
			</Row>
			<Feature title={v["feature"]["i1"]} value={v["feature"]["v1"]} />
			<Feature title={v["feature"]["i2"]} value={v["feature"]["v2"]} />
			<Feature title={v["feature"]["i3"]} value={v["feature"]["v3"]} />
			<Feature title={v["feature"]["i4"]} value={v["feature"]["v4"]} />
			<Feature
				title={v["feature"]["i5"]}
				value={v["feature"]["v5"]}
				color={"#eb4d4b"}
			/>
			<Row justify="center">
				<Col {...fullCol}>
					<Paragraph
						strong
						style={{
							textAlign: "center",
							display: "block",
							marginTop: "45px",
							fontSize: "15px",
						}}
					>
						{v["description"]}
					</Paragraph>
				</Col>
			</Row>
			<Row justify="center">
				<Col {...fullCol}>
					<Paragraph
						strong
						style={{
							textAlign: "center",
							display: "block",
							marginBottom: "45px",
							fontSize: "15px",
						}}
					>
						{v["exception"]}
					</Paragraph>
				</Col>
			</Row>

			<Row justify="center">
				<Col {...fullCol}>
					<ActionBtn onClick={toTop}>
						<Texty font={15}>처음부터 다시 해보기!</Texty>
					</ActionBtn>
				</Col>
			</Row>

			<Row justify="center">
				<Col xs={7} sm={7} md={4} lg={4} xl={4}>
					<ActionBtn>
						<a href={screenshotUrl} download>
							<Texty font={15}>이미지 저장</Texty>
						</a>
					</ActionBtn>
				</Col>
				<Col xs={1} sm={1} md={1} lg={1} xl={1}></Col>
				<Col xs={7} sm={7} md={4} lg={4} xl={4}>
					<ActionBtn
						onClick={toKakao}
						style={{ backgroundColor: "#FEE500" }}
					>
						<Texty strong font={15} color={"#000000"}>
							카카오 공유
						</Texty>
					</ActionBtn>
				</Col>
				<Col xs={1} sm={1} md={1} lg={1} xl={1}></Col>
				<Col xs={7} sm={7} md={4} lg={4} xl={4}>
					<FacebookProvider appId="311186230188498">
						<Share href="http://www.facebook.com">
							{({ handleClick }) => (
								<ActionBtn
									onClick={handleClick}
									style={{
										backgroundColor: "#4267B2",
									}}
								>
									<Texty font={15} color={"#FFFFFF"}>
										페이스북 공유
									</Texty>
								</ActionBtn>
							)}
						</Share>
					</FacebookProvider>
				</Col>
			</Row>

			<Row style={{ height: "400px" }}></Row>
		</>
	);
};

export default Result;
